mapper.select:
select sex as countName, count(*) as countNumber
from staff sf
join company cc
on cc.id=#{companyId}
GROUP BY sex;

select homeTown as countName, count(*) as countNumber
from staff sf
join company cc
on cc.id=#{companyId}
GROUP BY homeTown;

select incomeLevel as countName, count(*) as countNumber
from staff sf
join company cc
on cc.id=#{companyId}
GROUP BY incomeLevel;
